    private void jButtonOkActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonOkActionPerformed
        try {
            String cmd = "insert into Disciplinas (Disciplina, CargH, Curso, NVagas, Periodo) VALUES "
                    + "('"+jTextFieldDisc.getText()+"','"+jTextFieldCargH.getText()+"', '"+jTextFieldCurso.getText()+"',"
                    + " "+jTextFieldNVagas.getText()+", '"+jTextFieldPeriodo.getText()+"')";
            System.out.println(cmd);
            Connection con = ConexaoMySQL.getInstance().getConnection();
            con.createStatement().executeUpdate(cmd);
        } catch (SQLException ex) {
            ex.printStackTrace();
                     }       
    }//GEN-LAST:event_jButtonOkActionPerformed

